package javaPractice;

public class Switch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
   int day=2;
   
   switch (day) {
   
   case 1:
	   System.out.println("Mon");
	   break;
   case 2:
	   System.out.println("tue");
	   break;
   case 3:
	   System.out.println("wed");
       break; 
   case 4:
	   System.out.println("thur");
   case 5:
	   System.out.println("fri");
   }
	}

}
